package br.com.nelsonwilliam.dsp20191.chernobyl.presentation.utils;

import org.apache.tomcat.util.codec.binary.Base64;

import java.io.File;
import java.io.FileInputStream;

public class Utilitario {

    private static String IMG_SRC_PREFIX = "data:image/gif;base64,";

    private static String PERSON_IMG = "";
    private static String FIML_IMG = "";

    private static String encodeFileToBase64Binary(File file) throws Exception {
        try (FileInputStream fileInputStreamReader = new FileInputStream(file)) {
            byte[] bytes = new byte[(int) file.length()];
            fileInputStreamReader.read(bytes);
            return new String(Base64.encodeBase64(bytes), "UTF-8");
        }
    }

    public static String imageToString(File img) throws Exception {
        String base64 = encodeFileToBase64Binary(img);
        return IMG_SRC_PREFIX + base64;
    }

    public static String getFilmImgString() {
        return IMG_SRC_PREFIX + FIML_IMG;
    }

    public static String getPersonImgString() {
        return IMG_SRC_PREFIX + PERSON_IMG;
    }

}
